import React, { useEffect, useState } from 'react';

function Game() {

    const [canvas, setCanvas] = useState();
    const [size, setSize] = useState();

    let canvasRef = React.createRef();

    useEffect(() => {
        setCanvas(canvasRef.current);
        console.log("Canvas:", canvas)
        resizeCanvas();//do an initial resize for when it first renders, this function will also be called every time the window is resized too
    }, [canvasRef])

    useEffect(() => {
        window.addEventListener('resize', resizeCanvas)
    })

    function resizeCanvas() {
        if (canvas) {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
        }
    }

    if (canvas) {

        let c = canvas.getContext("2d");

        c.fillRect(100, 100, 100, 100);

        c.font = '30px Arial';

        let text = "Helllllo world! ! !!!"
        let totalOffset = 0;
        for (let i = 0; i < text.length; i++) {
            totalOffset += c.measureText(text[i]).width;
            c.fillText(text[i], window.innerWidth / 2 + (totalOffset),  window.innerHeight / 2);
        }
    }

    return (
        <canvas ref={canvasRef} id="game"></canvas>
    );
}

export default Game;
